mapper.select:
SELECT
SUM(CASE WHEN tb.km BETWEEN 1 AND 15 THEN 1 ELSE 0 END) AS 'count15' FROM (SELECT CAST(6371 * ACOS(COS(RADIANS(#{lat})) * COS(RADIANS(lat)) * COS(RADIANS(#{lng} - lng)) + SIN(RADIANS(#{lat})) * SIN(RADIANS(lat))) AS SIGNED) AS km from Restaurant r WHERE r.post_id IN(SELECT post_id FROM RestaurantMenu WHERE vegan_type IN
#{item}
)) AS tb

SELECT b.post_id, b.title, dist.km, im.server_file_name, v.vegan FROM Board b RIGHT JOIN (SELECT post_id,CAST(6371 * ACOS(COS(RADIANS(#{lat})) * COS(RADIANS(lat)) * COS(RADIANS(#{lng} - lng)) + SIN(RADIANS(#{lat})) * SIN(RADIANS(lat))) AS SIGNED) AS km FROM Restaurant) AS dist ON b.post_id = dist.post_id LEFT JOIN (SELECT idx, server_file_name FROM ImageTable it WHERE category ='식당' GROUP BY idx) AS im ON b.post_id = im.idx LEFT JOIN (SELECT post_id , GROUP_CONCAT(DISTINCT vegan_type ORDER by vegan_type SEPARATOR ',') AS vegan FROM RestaurantMenu GROUP BY post_id) AS v ON b.post_id =v.post_id WHERE b.post_id IN(SELECT post_id from RestaurantMenu where vegan_type IN(
#{item}
))AND dist.km < #{dist} AND b.is_hidden = 0 ORDER BY dist.km ASC LIMIT 20

SELECT b.post_id, m.nickname, m_img.server_file_name AS profile_image, b.content, b_img.server_file_name, tag.tag_content
FROM Board b
LEFT JOIN Members m ON m.user_no = b.user_no
LEFT JOIN (SELECT idx, server_file_name FROM ImageTable it WHERE category = '회원') AS m_img on m_img.idx = m.user_no
LEFT JOIN (SELECT idx, server_file_name FROM ImageTable it WHERE category = '피드' GROUP BY idx) AS b_img ON b_img.idx = b.post_id
LEFT JOIN (SELECT ft.post_id, GROUP_CONCAT(t.tag_content SEPARATOR ' ') AS tag_content FROM FeedTags ft LEFT JOIN Tags t ON ft.tag_id =t.tag_id GROUP BY ft.post_id) AS tag ON tag.post_id = b.post_id
WHERE (b.category = '피드' AND b.is_hidden = 0 AND(

		tag.tag_content LIKE #{item}

)) ORDER BY b.date DESC LIMIT 20;
